import React, { useState } from "react";
import { createJournal } from "../services/journalService";

const AddJournalForm = ({ onAdd }) => {
  const [mood, setMood] = useState("");
  const [stressLevel, setStressLevel] = useState("");
  const [note, setNote] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!mood || !stressLevel) {
      alert("Mood and Stress Level are required!");
      return;
    }

    try {
      await createJournal({
        mood,
        stressLevel: Number(stressLevel),
        note,
      });

      setMood("");
      setStressLevel("");
      setNote("");

      onAdd(); // refresh list
    } catch (err) {
      console.error("Error adding journal:", err.response?.data || err);
      alert("Error adding journal");
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
      <h3>Add New Journal</h3>

      <input
        type="text"
        placeholder="Mood"
        value={mood}
        onChange={(e) => setMood(e.target.value)}
        style={{ marginRight: "10px" }}
      />

      <input
        type="number"
        placeholder="Stress Level (1-10)"
        value={stressLevel}
        onChange={(e) => setStressLevel(e.target.value)}
        style={{ marginRight: "10px" }}
      />

      <input
        type="text"
        placeholder="Note (optional)"
        value={note}
        onChange={(e) => setNote(e.target.value)}
        style={{ marginRight: "10px" }}
      />

      <button type="submit">Add</button>
    </form>
  );
};

export default AddJournalForm;
