import React, { useEffect, useState } from "react";
import { getJournals, deleteJournal } from "../services/journalService";
import AddJournalForm from "./AddJournalForm";

const JournalList = () => {
  const [journals, setJournals] = useState([]);

  // Fetch all journals from backend
  const fetchJournals = async () => {
    try {
      const res = await getJournals();
      setJournals(res.data.data);
    } catch (err) {
      console.error("Error fetching journals:", err);
    }
  };

  // Delete a journal
  const handleDelete = async (id) => {
    try {
      await deleteJournal(id);
      fetchJournals();
    } catch (err) {
      console.error("Error deleting journal:", err);
    }
  };

  useEffect(() => {
    fetchJournals();
  }, []);

  return (
    <div style={{ maxWidth: "600px", margin: "0 auto" }}>
      {/* Add Journal Form */}
      <AddJournalForm onAdd={fetchJournals} />

      <h2>MindSpace Journals</h2>

      {journals.length === 0 ? (
        <p>No journals found. Add your first journal above!</p>
      ) : (
        journals.map((j) => (
          <div
            key={j._id}
            style={{
              border: "1px solid #ccc",
              padding: "10px",
              margin: "10px 0",
              borderRadius: "5px",
            }}
          >
            <p>
              <strong>Mood:</strong> {j.mood}
            </p>
            <p>
              <strong>Stress Level:</strong> {j.stressLevel}
            </p>
            <p>
              <strong>Note:</strong> {j.note}
            </p>
            <p>
              <strong>Date:</strong>{" "}
              {new Date(j.date).toLocaleString()}
            </p>
            <button onClick={() => handleDelete(j._id)}>Delete</button>
          </div>
        ))
      )}
    </div>
  );
};

export default JournalList;
