import React, { useState } from "react";
import { createJournal } from "../services/journalService";

const AddJournalForm = ({ onAdd }) => {
  const [mood, setMood] = useState("");
  const [stressLevel, setStressLevel] = useState("");
  const [note, setNote] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!mood || stressLevel === "") return alert("Mood & stress level required");

    try {
      await createJournal({ mood, stressLevel: Number(stressLevel), note });
      setMood(""); setStressLevel(""); setNote("");
      onAdd();
    } catch (err) {
      console.error(err);
      alert("Error adding journal");
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
      <h3>Add Journal</h3>
      <div>
        <label>Mood: </label>
        <select value={mood} onChange={(e) => setMood(e.target.value)}>
          <option value="">Select Mood</option>
          <option value="Happy">Happy</option>
          <option value="Sad">Sad</option>
          <option value="Anxious">Anxious</option>
          <option value="Relaxed">Relaxed</option>
          <option value="Angry">Angry</option>
          <option value="Neutral">Neutral</option>
        </select>
      </div>
      <div>
        <label>Stress Level (1-10): </label>
        <input type="number" min="1" max="10" value={stressLevel} onChange={(e) => setStressLevel(e.target.value)} />
      </div>
      <div>
        <label>Note: </label>
        <textarea value={note} onChange={(e) => setNote(e.target.value)} />
      </div>
      <button type="submit">Add</button>
    </form>
  );
};

export default AddJournalForm;
