import React, { useEffect, useState } from "react";
import { getJournals, deleteJournal } from "../services/journalService";
import AddJournalForm from "./AddJournalForm";
import EditJournalForm from "./EditJournalForm";

const JournalList = () => {
  const [journals, setJournals] = useState([]);
  const [editingJournal, setEditingJournal] = useState(null);

  const fetchJournals = async () => {
    try {
      const res = await getJournals();
      setJournals(res.data.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteJournal(id);
      fetchJournals();
    } catch (err) {
      console.error(err);
    }
  };

  const handleEdit = (journal) => setEditingJournal(journal);
  const cancelEdit = () => setEditingJournal(null);

  useEffect(() => { fetchJournals(); }, []);

  return (
    <div style={{ maxWidth: "600px", margin: "0 auto" }}>
      <AddJournalForm onAdd={fetchJournals} />
      {editingJournal && <EditJournalForm journal={editingJournal} onUpdate={fetchJournals} onCancel={cancelEdit} />}
      <h2>MindSpace Journals</h2>
      {journals.length === 0 ? <p>No journals found.</p> : journals.map((j) => (
        <div key={j._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px 0", borderRadius: "5px" }}>
          <p><strong>Mood:</strong> {j.mood}</p>
          <p><strong>Stress Level:</strong> {j.stressLevel}</p>
          <p><strong>Note:</strong> {j.note}</p>
          <p><strong>Date:</strong> {new Date(j.date).toLocaleString()}</p>
          <button onClick={() => handleEdit(j)} style={{ marginRight: "10px" }}>Edit</button>
          <button onClick={() => handleDelete(j._id)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default JournalList;
